mapper.select:
select id, bor.blacklist_id as blacklistId, bor.black_name as blackName, bor.identity_card as idCard, bor.mobile as mobile , bor.black_dept_name as blackDeptName, bor.black_company_name as blackCpyName, bor.black_time as blackTime, bor.black_reason as reason, bor.type as type, bor.operate_company_name as operateCpyName, bor.operate_dept_name as operateDeptName, bor.create_name as createName, bor.create_time as createTime from blacklist_operation_record as bor
AND bor.black_company_id = #{param.companyId}
AND bor.black_company_id
#{id}
AND (bor.black_dept_id Like CONCAT('', #{param.departId}, '\_%') or bor.black_dept_id=#{param.departId})
AND bor.black_user_id = #{param.userId}
AND bor.black_name LIKE CONCAT('%', #{param.blackName}, '%')
AND bor.create_name LIKE CONCAT('%', #{param.createName}, '%')
AND bor.type = #{param.type}
AND bor.create_time >= #{param.startTime}
AND bor.create_time <= #{param.endTime}
ORDER BY bor.create_time DESC

select black_name, identity_card, mobile, CONCAT(IF(black_company_name is not NULL,black_company_name,""), IF(black_dept_name is not NULL,black_dept_name,"")) AS blackCompanyDept, black_time, black_reason, if(type=1,'编辑','查询') AS `type`, CONCAT(IF(operate_company_name is not NULL,operate_company_name,""), IF(operate_dept_name is not NULL,operate_dept_name,"")) AS companyDept, create_name, create_time from blacklist_operation_record
1=1
AND id
#{id}
TRUE
AND company_id = #{param.companyId}
AND company_id
#{id}
AND (depart_id Like CONCAT('', #{param.departId}, '\_%') or depart_id=#{param.departId})
AND user_id = #{param.userId}
AND black_name LIKE CONCAT('%', #{param.blackName}, '%')
AND bor.create_name LIKE CONCAT('%', #{param.createName}, '%')
AND bor.type = #{param.type}
AND bor.create_time >= #{param.startTime}
AND bor.create_time <= #{param.endTime}

SELECT sb.id AS blacklistId, sb.user_name AS blackName, sb.user_id AS blackUserId, sb.mobile AS mobile, sb.id is not null as isBlack, sb.identity_card AS identityCard, sb.create_time AS blackTime, sb.company_id AS blackCompanyId, sci.company_name AS blackCompanyName, usd.department_id AS blackDeptId, sdi.depart_name AS blackDeptName, sb.reason AS blackReason FROM sys_user_info sui JOIN sys_staff_info ssi ON sui.id = ssi.user_id LEFT JOIN sys_company_info sci ON sci.id = ssi.comp_id LEFT JOIN union_staff_depart usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info sdi ON usd.department_id = sdi.id and sdi.status = 0 LEFT JOIN staff_blacklist AS sb ON (sb.mobile = sui.phone_number or sb.identity_card = sui.certificate_card) and sb.state = 'BLOCK'
ssi.deleted = 0 and sui.status != -1 and ssi.status = 1
AND ( FALSE
or sui.phone_number = #{param.mobile}
or sui.certificate_card = #{param.IdentityCard}
)
limit 0,1

SELECT ssi.comp_id AS operateCompanyId, sci.company_name AS operateCompanyName, usd.department_id AS operateDeptId, sdi.depart_name AS operateDeptName, sui.id AS operateUserId, sui.user_name AS operateUserName, sui.phone_number as mobile FROM sys_user_info AS sui JOIN sys_staff_info ssi ON sui.id = ssi.user_id LEFT JOIN sys_company_info AS sci ON ssi.comp_id = sci.id LEFT JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info AS sdi ON sdi.id = usd.department_id
ssi.deleted = 0 and sui.status != -1
AND ssi.id = #{param.staffId}
AND sui.id = #{param.userId}
limit 0,1
