mapper.select:
SELECT sui.id, ssi.id AS staff_id, sb.user_name, sci.company_name, sjp.position_name AS job_name, urs.role_name, sb.submit_user_name AS applicant_name, urs2.role_name AS applicant_role_name, sb.reason, ssi.avatar, sb.mobile, sdi.depart_name, sb.create_time, sb.approval_time, sb.state, sb.process_id, sb.process_key, sb.company_id, sb.type, sb.identity_card as identity_card, IFNULL(sui.certificate_card, sb.identity_card) as identityCard2, sb.id AS sb_id, sb.is_approve, sb.relieve_id , sb.relieve_key , 'staff-blacklist' AS image_type FROM staff_blacklist AS sb LEFT JOIN sys_staff_info AS ssi ON ssi.user_id = sb.user_id LEFT JOIN sys_company_info AS sci ON sb.company_id = sci.id LEFT JOIN sys_user_info AS sui ON ssi.user_id = sui.id LEFT JOIN union_role_staff AS urs ON urs.staff_id = ssi.id LEFT JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info AS sdi ON sdi.id = usd.department_id LEFT JOIN union_staff_position AS usp ON usp.staff_id = ssi.id LEFT JOIN sys_job_position AS sjp ON sjp.id = usp.position_id LEFT JOIN sys_staff_info AS ssi2 ON ssi2.user_id = sb.submit_user_id LEFT JOIN union_role_staff AS urs2 ON urs2.staff_id = ssi2.id LEFT JOIN union_staff_depart AS usd2 ON usd2.staff_id = ssi2.id
sb.state='BLOCK'
AND sb.company_id = #{param.companyId}
AND ( sb.user_name LIKE CONCAT('%', #{param.keyword}, '%') or sb.mobile LIKE CONCAT('%', #{param.keyword}, '%') )
AND sb.company_id
#{id}
AND (usd2.department_id LIKE CONCAT(#{param.departId}, '\_%') or usd2.department_id=#{param.departId})
AND ssi2.user_id = #{param.userId}
AND sb.user_name LIKE CONCAT('%', #{param.userName}, '%')
AND (usd.department_id LIKE CONCAT(#{param.departId}, '\_%') or usd.department_id=#{param.departId})
AND ssi.user_id = #{param.userId}
AND (sb.type = 1 OR sb.type = 2)
AND sb.type = #{param.type}
AND sb.mobile LIKE CONCAT('%', #{param.mobile}, '%')
AND sb.state = #{param.state}
ORDER BY sb.approval_time DESC

SELECT DISTINCT sui.id AS id, ssi.user_name AS userName, sci.company_name AS companyName, sui.phone_number, sdi.depart_name AS departName, ssi.status AS staffStatus, sb.id is not null as is_block, sb.reason FROM sys_user_info sui JOIN sys_staff_info ssi ON sui.id = ssi.user_id LEFT JOIN sys_company_info sci ON sci.id = ssi.comp_id LEFT JOIN union_role_staff urs ON urs.staff_id = ssi.id LEFT JOIN union_staff_depart usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info sdi ON usd.department_id = sdi.id and sdi.status = 0 LEFT JOIN staff_blacklist AS sb ON (sb.mobile = sui.phone_number or sb.identity_card = sui.certificate_card) AND sb.state = 'BLOCK'
ssi.deleted = 0 and sui.status != -1
AND sui.phone_number = #{param.mobile}
AND sui.certificate_card = #{param.IdentityCard}
limit 0,1

select ssi.user_id, ssi.user_name, ssi.id AS staffId, sci.company_name, sui.phone_number AS phone, sci.id is not null AS isInternalControl, sui.certificate_card, sdi.depart_name, ssi.comp_id as companyId, urs.role_name from sys_staff_info AS ssi LEFT JOIN sys_user_info AS sui ON ssi.user_id = sui.id LEFT JOIN sys_company_info AS sci ON ssi.comp_id = sci.id LEFT JOIN union_staff_depart AS usd ON ssi.id=usd.staff_id LEFT JOIN sys_department_info AS sdi ON sdi.id=usd.department_id LEFT JOIN union_role_staff AS urs ON ssi.id=urs.staff_id
AND sui.phone_number = #{param.phoneNumber}
AND sui.certificate_card = #{param.certificateCard}
order by sui.create_time desc limit 0,1
