SELECT COUNT( sui.id )as staffCountId , sci.company_name, sci.id as compId FROM sys_company_info AS sci LEFT JOIN sys_staff_info AS ssi ON sci.id = ssi.comp_id LEFT JOIN sys_user_info AS sui ON ssi.user_id = sui.id and ( sui.`status` = 0 OR sui.`status` = 3 ) where sci.status=0
AND sci.id
#{id}
AND sci.is_pro_data=#{isProData}
GROUP BY sci.id;

SELECT distinct sci.company_name, sci.id as compId FROM sys_company_info AS sci LEFT JOIN sys_staff_info AS ssi ON sci.id = ssi.comp_id LEFT JOIN sys_user_info AS sui ON ssi.user_id = sui.id and ( sui.`status` = 0 OR sui.`status` = 3 )
sci.status =0 and sci.is_pro_data =1
and sci.id = #{param.companyId}
and sci.id
#{id}

SELECT distinct sci.company_name as name, sci.id as companyId FROM sys_company_info AS sci LEFT JOIN sys_staff_info AS ssi ON sci.id = ssi.comp_id LEFT JOIN sys_user_info AS sui ON ssi.user_id = sui.id and ( sui.`status` = 0 OR sui.`status` = 3 )
sci.status =0 and sci.is_pro_data =1
and sci.id = #{param.companyId}
and sci.id
#{id}

SELECT COUNT( sui.id )AS staffCountId, sdi.depart_name, sci.company_name, sdi.id AS departmentId, ssis.user_name, sci.id AS copmId, ssi.user_type, sui.id AS userId FROM sys_company_info AS sci LEFT JOIN sys_department_info AS sdi ON sdi.comp_id = sci.id AND sdi.`status`!='-1' LEFT JOIN union_staff_depart AS usd ON (sdi.id = usd.department_id OR usd.department_id LIKE CONCAT(sdi.id, '\_%')) LEFT JOIN sys_staff_info AS ssi ON usd.staff_id = ssi.id and ssi.comp_id = sci.id LEFT JOIN sys_user_info AS sui ON ssi.user_id = sui.id and ( sui.`status` = 0 OR sui.`status` = 3 ) LEFT JOIN sys_staff_info AS ssis ON sdi.admin_account=ssis.id WHERE (sdi.parent_id IS NULL OR sdi.parent_id='')
AND sci.id=#{param.compId}
AND ssi.user_type=#{param.userType}
AND ssi.status=#{param.status}
AND sui.user_name=#{param.userName}
AND sui.phone_number=#{param.phoneNumber}
GROUP BY sdi.id;

SELECT COUNT( sui.id )AS staffCountId, sdi.depart_name, sci.company_name, sdi.id AS departmentId, ssis.user_name, sci.id AS copmId, ssi.user_type, sui.id AS userId FROM sys_company_info AS sci LEFT JOIN sys_department_info AS sdi ON sdi.comp_id = sci.id AND sdi.`status`!='-1' LEFT JOIN union_staff_depart AS usd ON (sdi.id = usd.department_id OR usd.department_id LIKE CONCAT(sdi.id, '\_%')) LEFT JOIN sys_staff_info AS ssi ON usd.staff_id = ssi.id and ssi.comp_id = sci.id LEFT JOIN sys_user_info AS sui ON ssi.user_id = sui.id and ( sui.`status` = 0 OR sui.`status` = 3 ) LEFT JOIN sys_staff_info AS ssis ON sdi.admin_account=ssis.id WHERE sdi.parent_id IS NOT NULL AND sdi.parent_id!=''
AND sdi.parent_id=#{param.departmentId}
AND sci.id=#{param.compId}
AND ssi.user_type=#{param.userType}
AND ssi.status=#{param.status}
AND sui.user_name=#{param.userName}
AND sui.phone_number=#{param.phoneNumber}
GROUP BY sdi.id;

SELECT sui.id AS id, ssi.avatar AS avatar, sui.avatar AS userAvatar, ssi.user_type, ssi.user_name AS userName, sui.phone_number AS phone, sjp.position_name AS positionName, urs.role_name AS roleName, sdi.depart_name AS departName, sci.company_name AS companyName, ssi.id AS staffId, ssi.status, sui.is_prepare_executive AS isPrepareExecutive, ssi.is_diamond_member FROM sys_staff_info ssi LEFT JOIN sys_user_info sui ON sui.id=ssi.user_id LEFT JOIN sys_company_info sci ON sci.id=ssi.comp_id LEFT JOIN union_role_staff urs ON urs.staff_id=ssi.id LEFT JOIN union_staff_position usp ON usp.staff_id = ssi.id LEFT JOIN sys_job_position sjp ON sjp.id = usp.position_id LEFT JOIN union_staff_depart usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info sdi ON sdi.id = usd.department_id
LEFT JOIN user_induction_record uir ON uir.leave_user_id = sui.id
sui.status = 1 sui.status IN (0, 3) sui.status IN (0, 1, 3)
and ssi.comp_id = #{param.companyId}
and ssi.user_type = #{param.userType}
and ssi.status = #{param.status}
and ssi.status IN (0, 3)
and (sdi.id = #{param.departmentId} OR sdi.id LIKE CONCAT(#{param.departmentId}, '\_%'))
AND sci.id
#{id}
AND sci.is_pro_data=#{param.isProData}
AND ssi.is_diamond_member=#{param.isDiamondMember}
AND sui.is_prepare_executive=#{param.isPrepareExecutive}
AND uir.approve_state='SUCCESS'

select sui.id AS id, ssi.user_type, ssi.user_name AS userName, sui.phone_number AS phone, sdi.depart_name AS departName,
sci.company_name AS companyName, ssi.id AS staffId, ssi.leave_reason, ssi.leave_type, ssi.date_join, ssi.date_left
from sys_staff_info ssi
JOIN sys_user_info sui ON sui.id = ssi.user_id
LEFT JOIN sys_company_info sci ON sci.id = ssi.comp_id
LEFT JOIN union_staff_position usp ON usp.staff_id = ssi.id
LEFT JOIN union_staff_depart usd ON usd.staff_id = ssi.id
LEFT JOIN sys_department_info sdi ON sdi.id = usd.department_id

    ssi.deleted =0 and sui.status !=-1 and ssi.status = 1

        and (ssi.user_name  like CONCAT('%', #{param.searchName} ,'%')
        or sui.phone_number like CONCAT('%', #{param.searchName}, '%'))


        and ssi.user_type = #{param.userType}


        and ssi.status = #{param.status}


        AND ssi.leave_type

            #{id}



        and ssi.comp_id = #{param.companyId}


        and (sdi.id = #{param.departmentId} OR sdi.id LIKE CONCAT(#{param.departmentId}, '\_%'))


        AND ssi.comp_id

            #{id}



        AND ssi.date_left >= #{param.begin}


        AND ssi.date_left < #{param.end}

    order by ssi.date_left desc
