mapper.select:
SELECT COUNT( sui.id )AS staffCountId, sdi.depart_name, sci.company_name, sdi.id AS departmentId, ssis.user_name, sci.id AS copmId, sui.id AS userId FROM sys_company_info AS sci LEFT JOIN sys_department_info AS sdi ON sdi.comp_id = sci.id AND sdi.`status`!='-1' LEFT JOIN union_staff_depart AS usd ON (sdi.id = usd.department_id OR usd.department_id LIKE CONCAT(sdi.id, '\_%')) LEFT JOIN sys_staff_info AS ssi ON usd.staff_id = ssi.id LEFT JOIN sys_user_info AS sui ON ssi.user_id = sui.id and ( sui.`status` = 0 OR sui.`status` = 3 ) LEFT JOIN sys_staff_info AS ssis ON sdi.admin_account=ssis.id WHERE (sdi.parent_id IS NULL OR sdi.parent_id='')
AND sci.id=#{id}
GROUP BY sdi.id;

SELECT COUNT( sui.id )AS staffCountId, sdi.depart_name, sci.company_name, sdi.id AS departmentId, ssis.user_name, sci.id AS copmId, sui.id AS userId FROM sys_company_info AS sci LEFT JOIN sys_department_info AS sdi ON sdi.comp_id = sci.id AND sdi.`status`!='-1' LEFT JOIN union_staff_depart AS usd ON (sdi.id = usd.department_id OR usd.department_id LIKE CONCAT(sdi.id, '\_%')) LEFT JOIN sys_staff_info AS ssi ON usd.staff_id = ssi.id LEFT JOIN sys_user_info AS sui ON ssi.user_id = sui.id and ( sui.`status` = 0 OR sui.`status` = 3 ) LEFT JOIN sys_staff_info AS ssis ON sdi.admin_account=ssis.id WHERE sdi.parent_id IS NOT NULL AND sdi.parent_id!=''
AND sdi.parent_id=#{departmentId}
AND sci.id=#{id}
GROUP BY sdi.id;
