mapper.update:
UPDATE sys_company_info SET is_edit= 0
id in
#{company.id}

mapper.select:
SELECT sci.id, sci.company_name, sci.company_code, sci.company_addr_province_code, sci.company_address_city_code, sci.company_address_area_code, sci.company_address, sci.contact_name, sci.contact_phone, sci.status FROM sys_company_info AS sci
sci.status = 0 and sci.company_address_city_code in
#{code}

SELECT ci.id AS companyId, ci.company_name FROM sys_company_info AS ci
ci.id IN
#{id}

SELECT id AS companyId, company_name AS companyName, 1 AS type FROM sys_company_info
status = 0
AND id = #{companyId}
