mapper.update:
UPDATE sys_department_info SET is_edit = 0
id IN
#{dept.id}

mapper.select:
SELECT sdi.id, sdi.depart_name, psdi.depart_name parentDep, sdi.telephone, sdi.depart_code, sdi.depart_desc, ( SELECT count(id) FROM union_staff_depart usd WHERE sdi.id = usd.department_id ) staffCount FROM sys_department_info sdi left join sys_department_info psdi on psdi.parent_id = sdi.id
sdi.status != -1
and comp_id = #{dep.compId}

SELECT sdi.id, sdi.depart_name, sdi.parent_id FROM sys_department_info AS sdi
sdi.status = 0 and sdi.comp_id = #{user.companyId}
and id = #{user.deptId} or sdi.parent_id like concat('',#{user.deptId},'\_%')

SELECT sdi.depart_name AS departName, sdi.id AS departId, 2 AS type FROM sys_department_info AS sdi
sdi.status = 0 AND sdi.is_show_business = 1
AND sdi.comp_id = #{companyId}
AND sdi.parent_id = #{departId}
AND (sdi.parent_id IS NULL OR sdi.parent_id = '')

SELECT sdi.id, sdi.depart_name AS `name` FROM sys_department_info AS sdi WHERE status = 0 AND sdi.is_show_business = 1 AND sdi.comp_id = #{user.companyId}
AND (sdi.parent_id IS NULL OR sdi.parent_id = '')
AND sdi.parent_id = #{user.deptId}

SELECT sdi.id, sdi.depart_name AS `name` FROM sys_department_info AS sdi
sdi.status = 0 AND sdi.is_show_business = 1
AND sdi.parent_id = #{departId}
AND ( sdi.parent_id IS NULL OR sdi.parent_id = '' ) AND sdi.comp_id = #{companyId}
GROUP BY sdi.id

SELECT sdi.id, sdi.comp_id, sdi.depart_name, sdi.parent_id, sdi.telephone, sdi.depart_code, sdi.admin_account, ssi.user_name AS adminName, sdi.is_show_business FROM sys_department_info AS sdi LEFT JOIN sys_staff_info AS ssi ON ssi.id = sdi.admin_account and ssi.status in (0, 3) LEFT JOIN sys_user_info AS sui ON sui.id = ssi.user_id AND sui.status != -1
sdi.status = 0 and sdi.comp_id = #{companyId}
and (sdi.id LIKE CONCAT(#{departId}, '\_%') or sdi.id=#{departId})
and sdi.id=#{departId}
