SELECT sui.id AS id, ssi.comp_id AS compId, ssi.user_name AS userName, sci.company_name AS companyName, sui.phone_number AS phone, sjp.position_name AS positionName, sdi.depart_name AS departName, ssi.id AS staffId, urs.role_name AS roleName, sui.status AS staffStatus, sui.status = 0 AS activity, sui.status = 3 AS teamwork, sui.status = 1 AS stops, ssi.id = #{staff.staffId} AS isAdmin, sb.id is not null AS is_block, ssi.evaluate, ssi.resume_url FROM sys_user_info sui JOIN sys_staff_info ssi ON sui.id = ssi.user_id LEFT JOIN sys_company_info sci ON sci.id = ssi.comp_id LEFT JOIN union_role_staff urs ON urs.staff_id = ssi.id LEFT JOIN union_staff_depart usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info sdi ON usd.department_id = sdi.id and sdi.status = 0 LEFT JOIN union_staff_position usp ON usp.staff_id = ssi.id LEFT JOIN sys_job_position sjp ON sjp.id = usp.position_id and sjp.status = 0 LEFT JOIN staff_blacklist AS sb ON sb.user_id = sui.id AND sb.state = 'BLOCK'
ssi.deleted = 0 and sui.status != -1
and sci.id = #{staff.companyId}
and sdi.id like concat('', #{staff.departId}, '%')
and sjp.id = #{staff.jobId}
and urs.role_id = #{staff.roleId}
and ssi.user_name like concat('%', #{staff.staffName}, '%')
and sui.phone_number like concat('%', #{staff.phoneNumber}, '%')
order by isAdmin DESC, stops, teamwork, activity, ssi.create_time desc

SELECT ssi.comp_id AS compId, ssi.user_name AS userName, sui.phone_number AS phone, sjp.position_name AS positionName, sdi.depart_name AS departName, ssi.id AS staffId FROM sys_staff_info ssi LEFT JOIN sys_user_info sui ON sui.id=ssi.user_id LEFT JOIN union_staff_position usp ON usp.staff_id = ssi.id LEFT JOIN sys_job_position sjp ON sjp.id = usp.position_id LEFT JOIN union_staff_depart usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info sdi ON sdi.id = usd.department_id
ssi.deleted = 0
and usp.position_id = #{positionId}
and usd.department_id = #{departId}
and ssi.comp_id = #{companyId}

SELECT sui.id AS id, sui.avatar AS avatar, ssi.user_name AS userName, sui.phone_number AS phone, sjp.position_name AS positionName, urs.role_name AS roleName, sdi.depart_name AS departName, ssi.id AS staffId FROM sys_staff_info ssi LEFT JOIN sys_user_info sui ON sui.id=ssi.user_id LEFT JOIN union_role_staff urs ON urs.staff_id=ssi.id LEFT JOIN union_staff_position usp ON usp.staff_id = ssi.id LEFT JOIN sys_job_position sjp ON sjp.id = usp.position_id LEFT JOIN union_staff_depart usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info sdi ON sdi.id = usd.department_id
ssi.deleted = 0 and sui.status in (0,3)
and ssi.comp_id = #{companyId}

SELECT sui.id AS userId, sui.user_name AS name, sdi.depart_name AS departName, sjp.position_name AS jobName FROM sys_user_info AS sui JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id and ssi.status = 0 JOIN sys_company_info sci ON sci.id=ssi.comp_id
JOIN company_admin AS ca ON ca.company_id = sci.id and ssi.id != ca.staff_id
LEFT JOIN union_staff_position usp ON usp.staff_id = ssi.id LEFT JOIN sys_job_position sjp ON sjp.id = usp.position_id LEFT JOIN union_staff_depart usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info sdi ON sdi.id = usd.department_id
sci.id = #{compId}
and sdi.id in
#{id}

SELECT sui.id, sui.user_name, sui.phone_number, urs.role_name FROM sys_user_info AS sui JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id LEFT JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id LEFT JOIN union_role_staff AS urs ON urs.staff_id = ssi.id
sui.status IN (0, 2)
AND usd.department_id LIKE CONCAT('',#{departId},'%')
AND urs.role_id = #{roleId}
AND ssi.comp_id = #{companyId}

SELECT sui.id, sui.user_name, urs.role_name FROM sys_staff_info AS ssi JOIN sys_user_info AS sui ON ssi.user_id = sui.id JOIN union_role_staff AS urs ON urs.staff_id = ssi.id
sui.status in (0, 3) and ssi.id in
#{id}

SELECT sui.id AS userId, sui.user_name, sci.company_name, sdi.depart_name AS departmentName, urs.role_id, urs.role_name FROM sys_user_info AS sui JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id LEFT JOIN union_role_staff AS urs ON urs.staff_id = ssi.id JOIN sys_company_info AS sci ON sci.id = ssi.comp_id LEFT JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info AS sdi ON sdi.id = usd.department_id
sui.status IN (0, 3) AND sui.is_sign_order = 1
AND sci.id = #{param.companyId}
AND sdi.id = #{param.departId}
AND sui.id = #{param.userId}
AND sui.user_name LIKE CONCAT('%', #{param.name}, '%')
AND sci.status != -1 ORDER BY sui.create_time DESC

SELECT sui.user_name, sui.id AS userId, sci.company_name, sdi.id AS departId, sci.id AS companyId, sdi.depart_name AS departmentName, sui.phone_number AS mobile
FROM sys_staff_info AS ssi LEFT JOIN sys_company_info AS sci ON sci.id = ssi.comp_id LEFT JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info AS sdi ON sdi.id = usd.department_id JOIN sys_user_info AS sui ON sui.id = ssi.user_id
sui.id = #{id}

SELECT ssi.user_id AS userId, ssi.user_name AS userName, #{departId} AS depart_id, 3 AS type FROM sys_staff_info AS ssi JOIN sys_user_info AS sui ON sui.id = ssi.user_id
JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id
(sui.status = 0 OR sui.status = 3)
AND usd.department_id = #{departId}
AND NOT EXISTS ( SELECT * FROM union_staff_depart AS usd JOIN sys_department_info AS sdi ON sdi.id = usd.department_id WHERE usd.staff_id = ssi.id AND sdi.comp_id = #{companyId} ) AND ssi.comp_id = #{companyId}
AND ssi.user_id != #{userId}

SELECT sui.id AS id, ssi.comp_id AS compId, ssi.user_name AS userName, sci.company_name AS companyName, sui.phone_number AS phone, sjp.position_name AS positionName, sdi.depart_name AS departName, ssi.id AS staffId, urs.role_name AS roleName, sui.status AS staffStatus, sui.status = 0 AS activity, sui.status = 3 AS teamwork, sui.status = 1 AS stops, ssi.id = #{staff.staffId} AS isAdmin, ssi.avatar AS staff_avatar, sui.avatar AS user_avatar FROM sys_user_info sui JOIN sys_staff_info ssi ON sui.id = ssi.user_id LEFT JOIN sys_company_info sci ON sci.id = ssi.comp_id LEFT JOIN union_role_staff urs ON urs.staff_id = ssi.id LEFT JOIN union_staff_depart usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info sdi ON usd.department_id = sdi.id and sdi.status = 0 LEFT JOIN union_staff_position usp ON usp.staff_id = ssi.id LEFT JOIN sys_job_position sjp ON sjp.id = usp.position_id and sjp.status = 0
ssi.deleted = 0 and (sui.status = 0 OR sui.status = 3)
and sci.id = #{staff.companyId}
and sdi.id like concat('', #{staff.departId}, '%')
and sui.id = #{staff.userId}
and sjp.id = #{staff.jobId}
and urs.role_id = #{staff.roleId}
and ssi.user_name like concat('%', #{staff.staffName}, '%')
and sui.phone_number like concat('%', #{staff.phoneNumber}, '%')
AND ( sui.user_name LIKE CONCAT('%', #{staff.key}, '%') OR sui.phone_number LIKE CONCAT('%', #{staff.key}, '%') )
order by isAdmin DESC, stops, teamwork, activity, ssi.create_time desc

SELECT sui.avatar AS user_avatar, ssi.avatar AS staff_avatar, sui.user_name, sui.id AS userId, sci.company_name, sdi.id AS departId, sci.id AS companyId, sdi.depart_name AS departmentName, sui.phone_number AS mobile
FROM sys_staff_info AS ssi LEFT JOIN sys_company_info AS sci ON sci.id = ssi.comp_id LEFT JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info AS sdi ON sdi.id = usd.department_id JOIN sys_user_info AS sui ON sui.id = ssi.user_id
#{id}

