mapper.select:
SELECT ssi.comp_id AS compId, ssi.user_name AS userName, sci.company_name AS companyName, sui.phone_number AS phone, sjp.position_name AS positionName, sdi.depart_name AS departName, ssi.id AS staffId, urs.role_name AS roleName FROM sys_user_info sui JOIN sys_staff_info ssi ON sui.id = ssi.user_id LEFT JOIN sys_company_info sci ON sci.id = ssi.comp_id LEFT JOIN union_role_staff urs ON urs.staff_id = ssi.id LEFT JOIN union_staff_depart usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info sdi ON usd.department_id = sdi.id and sdi.status = 1 LEFT JOIN union_staff_position usp ON usp.staff_id = ssi.id LEFT JOIN sys_job_position sjp ON sjp.id = usp.position_id and sjp.status = 1
ssi.status != -1
and sci.id = #{staff.companyId}
and sdi.id = #{staff.departId}
and sjp.id = #{staff.jobId}
and urs.role_id = #{staff.roleId}
and ssi.user_name like concat('%', #{staff.staffName}, '%')
and sui.phone_number like concat('%', #{staff.phoneNumber}, '%')

SELECT ssi.comp_id AS compId, ssi.user_name AS userName, sui.phone_number AS phone, sjp.position_name AS positionName, sdi.depart_name AS departName, ssi.id AS staffId FROM sys_staff_info ssi LEFT JOIN sys_user_info sui ON sui.id=ssi.user_id LEFT JOIN union_staff_position usp ON usp.staff_id = ssi.id LEFT JOIN sys_job_position sjp ON sjp.id = usp.position_id LEFT JOIN union_staff_depart usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info sdi ON sdi.id = usd.department_id
ssi.status != -1
and usp.position_id = #{positionId}
and usd.department_id = #{departId}
and ssi.comp_id = #{companyId}
