mapper.select:
select 
from sys_user_info

  status != -1

    and id != #{userInfo.id}

  or ( 1 = 2

    or phone_number = #{userInfo.phoneNumber}

  )

SELECT ssi.user_id FROM sys_staff_info AS ssi JOIN union_staff_depart AS usd ON ssi.id = usd.staff_id JOIN union_staff_position AS usp ON ssi.id = usp.staff_id JOIN sys_jop_position AS sjp ON sjp.id = usp.position_id and sjp.status = 1 JOIN sys_department_info AS sdi ON sdi.id = usd.department_id
sdi.id IN
#{dep.id}
