mapper.update:
UPDATE sys_user_info SET is_edit = 0
id IN
#{user.id}

mapper.select:
select

from sys_user_info

    status in (0, 3)

        and id != #{userInfo.id}


        and phone_number = #{userInfo.phoneNumber}

SELECT ssi.user_id FROM sys_staff_info AS ssi JOIN union_staff_depart AS usd ON ssi.id = usd.staff_id JOIN union_staff_position AS usp ON ssi.id = usp.staff_id JOIN sys_jop_position AS sjp ON sjp.id = usp.position_id and sjp.status = 1 JOIN sys_department_info AS sdi ON sdi.id = usd.department_id
sdi.id IN
#{dep.id}

SELECT sui.id AS user_id, sui.user_name, sui.phone_number AS mobile, sdi.depart_name, sjp.position_name AS positionName, urs.role_name AS roleName, sci.company_name AS companyName, ssi.avatar, ssi.status FROM sys_user_info AS sui JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id JOIN sys_department_info AS sdi ON sdi.id = usd.department_id LEFT JOIN sys_company_info sci ON sci.id = ssi.comp_id LEFT JOIN union_role_staff urs ON urs.staff_id = ssi.id LEFT JOIN union_staff_position usp ON usp.staff_id = ssi.id LEFT JOIN sys_job_position sjp ON sjp.id = usp.position_id
sui.status = 1 sui.status IN (0, 3) sui.status IN (0, 1, 3)
AND sdi.comp_id = #{param.companyId}
AND usd.department_id = #{param.departmentId}
and sdi.depart_name = #{param.departName}
AND (usd.department_id LIKE CONCAT(#{param.departId} , '\_%') or usd.department_id=#{param.departId})
AND sui.id != #{param.userId}

SELECT sui.id, sui.user_name AS `name` FROM sys_user_info AS sui JOIN sys_staff_info AS ssi ON sui.id = ssi.user_id JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id
AND sui.status IN (0, 3)
AND usd.department_id = #{departId}
GROUP BY sui.id ORDER BY sui.create_time DESC;

SELECT sui.id, sui.user_name AS `name`, sci.id AS company_id, sci.company_name, COUNT(ssi.id) AS salesman_num FROM sys_user_info AS sui JOIN sys_staff_info AS ssi ON sui.id = ssi.user_id LEFT JOIN union_platform_role_staff AS urs ON urs.staff_id = ssi.id LEFT JOIN sys_company_info AS sci ON sci.id = ssi.comp_id
AND urs.id
#{id}
AND sui.status IN (0, 3)
AND sui.id = #{userId}
ORDER BY sui.create_time DESC;

SELECT sui.id FROM sys_user_info AS sui JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id JOIN union_role_staff AS urs ON urs.staff_id = ssi.id
sui.status IN (0, 3)
AND urs.role_name = #{roleName}

SELECT urs.id, urs.staff_id, urs.role_id, urs.role_name FROM sys_user_info AS sui JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id JOIN union_role_staff AS urs ON urs.staff_id = ssi.id
ssi.deleted = 0 and sui.status != -1
and sui.id = #{userId}

SELECT sui.id AS user_id, sui.user_name, sui.phone_number AS mobile, sdi.depart_name, sjp.position_name AS positionName, urs.role_name AS roleName, sci.company_name AS companyName, ssi.avatar, ssi.status FROM sys_user_info AS sui JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id JOIN sys_department_info AS sdi ON sdi.id = usd.department_id LEFT JOIN sys_company_info sci ON sci.id = ssi.comp_id LEFT JOIN union_role_staff urs ON urs.staff_id = ssi.id LEFT JOIN union_staff_position usp ON usp.staff_id = ssi.id LEFT JOIN sys_job_position sjp ON sjp.id = usp.position_id
sui.status = 1 sui.status IN (0, 3) sui.status IN (0, 1, 3)
AND sdi.comp_id = #{param.companyId}
AND (usd.department_id LIKE CONCAT(#{param.departId} , '\_%') or usd.department_id=#{param.departId})
and sdi.depart_name = #{param.departName}
AND sui.id != #{param.userId}

 SELECT count(1) > 0
FROM sys_user_info
WHERE  `status` in (0, 3) and  (  phone_number = #{mobile}

    or certificate_card = #{certificateCard}

)

SELECT sui.train_no FROM sys_user_info AS sui JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id where sui.is_deleted=0
and ssi.comp_id = #{companyId}
and sui.train_no is not null and sui.status !=-1 group by sui.train_no order by sui.train_no desc
