mapper.select:
select ui.id,sui.user_name as 'approveName', ui.approve_state as 'approveState', ui.sign_time as 'approveTime', ui.process_id, ui.process_key from user_induction_record ui join sys_user_info sui on ui.leave_user_id = sui.id
and ui.approve_state = #{queryDto.approveState}
and ui.create_by = #{queryDto.userId}
and DATE_FORMAT(ui.sign_time,'%Y-%m-%d') >= DATE_FORMAT(#{queryDto.begin},'%Y-%m-%d')
and DATE_FORMAT(ui.sign_time,'%Y-%m-%d') < DATE_FORMAT(#{queryDto.end},'%Y-%m-%d')
order by ui.sign_time desc, ui.create_time desc

select distinct ssi.id as staffId, ssi.leave_type from user_induction_record as uir join sys_user_info as sui on sui.id = uir.leave_user_id join sys_staff_info as ssi on ssi.user_id = sui.id left join union_staff_depart AS usd ON usd.staff_id = ssi.id
uir.approve_state='SUCCESS'
AND ssi.comp_id = #{param.companyId}
AND (usd.department_id = #{param.departId} OR usd.department_id LIKE CONCAT(#{param.departId}, '\_%'))
AND ssi.comp_id
#{id}
