mapper.select:
select o.*,uop.name as 'type_name', (SELECT content from user_reply r where r.opinion_id = o.id ORDER BY r.create_time desc limit 1 ) as reply_content from user_opinion o LEFT JOIN user_opinion_type uop on o.type_id = uop.id where o.user_id = #{query.userId} and o.deleted = 1
and o.type_id = #{query.typeId}
order by o.create_time desc

select uo.*,sci.company_name, sdi.depart_name,uop.name as 'type_name', (SELECT content from user_reply r where r.opinion_id = uo.id ORDER BY r.create_time desc limit 1 ) as reply_content from user_opinion uo LEFT JOIN user_opinion_type uop on uo.type_id = uop.id LEFT JOIN user_opinion_ref uor on uor.opinion_id = uo.id LEFT JOIN sys_staff_info ssi ON uo.user_id=ssi.user_id LEFT JOIN sys_company_info sci ON ssi.comp_id=sci.id LEFT JOIN union_staff_depart usd ON usd.staff_id = ssi.id LEFT JOIN sys_department_info sdi ON usd.department_id = sdi.id
uo.deleted = 1
and uor.user_id = #{query.userId}
and uo.user_name like concat('%',#{query.searchName},'%')
and uo.type_id = #{query.typeId}
order by uo.create_time desc
