mapper.select:
SELECT
    count(1)
FROM sys_user_info AS sui
JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id
LEFT JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id

    sui.status in (0, 3)

        AND ssi.comp_id = #{param.companyId}


        AND (usd.department_id = #{param1.departId} OR usd.department_id LIKE CONCAT(#{param1.departId}, '\_%'))


        AND ssi.date_join >= #{param.begin}


        AND ssi.date_join < #{param.end}



            #{companyId}

SELECT
    count(1)
FROM sys_user_info AS sui
JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id
LEFT JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id

    sui.status = 1

        AND ssi.comp_id = #{param.companyId}


        AND (usd.department_id = #{param1.departId} OR usd.department_id LIKE CONCAT(#{param1.departId}, '\_%'))


        AND ssi.date_left >= #{param.begin}


        AND ssi.date_left < #{param.end}



            #{companyId}
