SELECT
    count(1)
FROM sys_user_info AS sui
JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id
LEFT JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id

    sui.status in (0, 3)

        AND ssi.comp_id = #{param.companyId}


        AND (usd.department_id = #{param.departId} OR usd.department_id LIKE CONCAT(#{param.departId}, '\_%'))


        AND ssi.date_join >= #{param.begin}


        AND ssi.date_join < #{param.end}



            #{companyId}

SELECT count(1) FROM sys_user_info AS sui JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id LEFT JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id
( (sui.STATUS IN ( 0, 3 ) AND ssi.date_join < #{param.end}) or(sui.`status` = 1 and ssi.date_left >= #{param.end}) )
AND ssi.comp_id = #{param.companyId}
AND (usd.department_id = #{param.departId} OR usd.department_id LIKE CONCAT(#{param.departId}, '\_%'))
#{companyId}

SELECT count(1) FROM sys_user_info AS sui JOIN sys_staff_info AS ssi ON ssi.user_id = sui.id LEFT JOIN union_staff_depart AS usd ON usd.staff_id = ssi.id
sui.status IN (0, 3)
and ssi.comp_id = #{param.companyId}
and (usd.department_id = #{param.departId} OR usd.department_id LIKE CONCAT(#{param.departId}, '\_%'))
and sui.highest_education = #{param.highestEducation}
or sui.highest_education = 'PRIMARY_SCHOOL' or sui.highest_education = 'JUNIOR_HIGH' or sui.highest_education = 'TECHNICAL_SECONDARY' or sui.highest_education = 'SENIOR_HIGH'
#{companyId}

